import React from 'react';
import './Measure.css';


const Measure = ({measure}) =>{

    let color = null;

    if(measure.systolic > 70 && measure.diastolic > 40 | measure.systolic < 90 && measure.diastolic < 60) {
        color= {backgroundColor: `#1D90C5`};
    } else if (measure.systolic > 90 && measure.diastolic > 60 | measure.systolic < 120 && measure.diastolic < 80) {
        color={backgroundColor: `#33AF1A`};
    } else if (measure.systolic > 120 && measure.diastolic > 80 | measure.systolic < 140 && measure.diastolic < 100) {
        color={backgroundColor: `#FFC91B`};
    } else if (measure.systolic > 140 && measure.diastolic > 100) {
        color={backgroundColor: `#FF2929`};
    }

    return(
        <div className="measure">
            <div className='measure-status' style={color}>
                <div className="result-measure-blood-pressure">
                    <p>{measure.systolic}/{measure.diastolic}</p>
                </div>
                <div className="result-measure-pulse">
                    <p>{measure.pulse}</p>
                </div>
            </div>  
            <div className="result-measure-info">
            <p className="result-measure-description">{measure.description}</p>
                <p className="result-measure-date">{measure.date}</p>
            </div>
        </div>     
    )
}

export default Measure
